mapper.delete:
DELETE FROM e_mail

    AND e_mail_id = #{emailId}
    AND accept_person_id = #{acceptPersonId}
    AND accept_person_name = #{acceptPersonName}
    AND send_person_id = #{sendPersonId}
    AND send_person_name = #{sendPersonName}
    AND e_mail_theme = #{emailTheme}
    AND send_information = #{sendInformation}
    AND send_time = #{sendTime}
    AND isread = #{isRead}
    AND autosend = #{autoSend}
    AND sendoraccept = #{sendOrAccept}

mapper.update:
UPDATE e_mail

     accept_person_id = #{acceptPersonId}
     accept_person_name = #{acceptPersonName}
     send_person_id = #{sendPersonId}
     send_person_name = #{sendPersonName}
     e_mail_theme = #{emailTheme}
     send_information = #{sendInformation}
     send_time = #{sendTime}
     isread = #{isRead}
     autosend = #{autoSend}
     sendoraccept = #{sendOrAccept}

WHERE e_mail_id = #{emailId}

mapper.select:
SELECT * FROM e_mail

    AND e_mail_id = #{emailId}
    AND accept_person_id = #{acceptPersonId}
    AND accept_person_name = #{acceptPersonName}
    AND send_person_id = #{sendPersonId}
    AND send_person_name = #{sendPersonName}
    AND e_mail_theme = #{emailTheme}
    AND send_information = #{sendInformation}
    AND send_time = #{sendTime}
    AND isread = #{isRead}
    AND autosend = #{autoSend}
    AND sendoraccept = #{sendOrAccept}
